import React, { useEffect, useState } from 'react';
import PostsCard from './PostsCard';

export default function Post() {
  const [value, setValue] = useState(0);

  const [Post , setPost] = useState([])

  useEffect(() => {
    const token = sessionStorage.getItem('accessToken');
    const getAllPosts = async()=>{

        const data = await fetch('http://127.0.0.1:8000/createPost/Post' , {
            method:'GET',
            headers:{
              "Authorization": `${token}`
            }
        })
        const res = await data.json()
        if(res.status==="Success"){
          setPost(res.post)
        }
        console.log(res.post)
        // setPost
    }

    getAllPosts()
        
  }, []);

  return (
    <>
      {
        Post && Post.length > 0 ?  Post.map((data , index)=>{

          return (
            <div key={index} className="border">
                        <div class="card" style={{ width: "18rem" }}>
          <img
            class="card-img-top"
            src="https://images.unsplash.com/uploads/141103282695035fa1380/95cdfeef?auto=format&fit=crop&q=80&w=2030&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D"
            alt="Card image cap"
          />
          <div class="card-body">
            <h5 class="card-title">Card title</h5>
            <p class="card-text">
              Some quick example text to build on the card title and make up
              the bulk of the card's content.
            </p>
            <a href="#" class="btn btn-primary">
              Go somewhere
            </a>
          </div>
        </div>
            </div>
          )

        }) : <h1>No Data Available</h1>
      }
    </>
  );
}
