import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import '../../css/PostDetail.css'

export default function PostDetail() {

    const {id} = useParams()
    console.log(id)

    const [post ,setPost ]= useState()

    useEffect(() => {
        const token = sessionStorage.getItem('accessToken');
    
       const getInfoWithid =  async ()=>{
        const data = await fetch(`http://127.0.0.1:8000/postDetail/${id}` , {
            method:"GET",
            headers:{
                Authorization:token
            }
        })
        
        const res = await data.json()
        console.log(res.info)

        if(res.status==='Success'){
          setPost(res.info)
        }
    
       }
       getInfoWithid()
    
      }, []);
    


  return (
    <>
    <div className="border border-danger PostDetailContainer  p-2 my-4 container">
      <h1 style={{letterSpacing:"1px" , fontSize:"35px" , fontWeight:"900"}} className='text-center'>Heading</h1>

      <div style={{display:"flex" , justifyContent:"space-around"}} className='border border-primary  container'>

        <p> <b> 10/21/22 </b></p>
        <p><b> Sports </b></p>
      </div>

    </div>


    <div className="container">
      <img src="" alt="" />
    </div>
    </>
  )
}
