import React, { useEffect, useState } from 'react';
import PostsCard from './PostsCard';
import { useSearchParams } from 'react-router-dom';

export default function Post() {

  const [Post , setPost] = useState([])
  const [searchParams] = useSearchParams()
  const Searchcategory = searchParams.get('category')
  console.log(Searchcategory)


  

  useEffect(() => {

    const token = sessionStorage.getItem('accessToken');
    const getPosts = async(category)=>{

      let url = 'http://127.0.0.1:8000/AllPosts'

      if(category){
        url = `http://127.0.0.1:8000/Post?category=${Searchcategory}`
      }


        const data = await fetch(url , {
            method:'GET',
            headers:{
              "Authorization": `${token}`
            },

        })
        const res = await data.json()
        if(res.status==="Success"){
          setPost(res.post)
        }
        console.log(res.post)
     
        // setPost
    }
    getPosts(Searchcategory)
        
  }, [Searchcategory]);

  return (

    <>

    {
      Post && Post.length > 0 ? <div className="row">
      {Post.map((data,index)=>{
      return <div className="col-md-4" key={index}>
        <PostsCard data={data} />
      </div>
    })}
    </div> : <div style={{display:"flex" , justifyContent:"center" , textAlign:"start"}}> <h1 className='my-5'>No Current Blogs</h1> </div>
    }
    
    </>

  );
}
