import React, { useContext, useRef, useState } from "react";
import { InfoContext } from "../Context/InfoProvider";
import '../css/EditProfile.css'

export default function MyProfile() {
  const { Account } = useContext(InfoContext);
  const fileInputRef = useRef(null);

  const initialData = {
    About1 : "" , 
    About2 : "" ,
    About3 : ""
  }
  const [data , setData] = useState(initialData)

  const handleOnUpdate = async() => {
    console.log("Profile Updated");
    console.log(data)
    const res = await fetch("http://127.0.0.1:8000/UpdateProfile" , {
      method:"POST",
      body:JSON.stringify(data),
      headers:{
        "Content-type":"application/json"
      }
    })

    const info = await res.json()
    console.log(info)

  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    // Handle the file upload, e.g., update the user's profile picture
    console.log("File uploaded:", file);
  };


  const hanldeOnChange = (e)=>{
    setData({...data,[e.target.name]:e.target.value})
  }

  return (
    <>
      <section className="h-100 gradient-custom-2">
        <div className="container-fluid py-5 h-100">
          <div className="row d-flex justify-content-center align-items-center h-100">
            <div className="col col-lg-9 col-xl-7">
              <div className="card">
                <div
                  className="rounded-top text-white d-flex flex-row border border-danger"
                  style={{ backgroundColor: "#000", height: "200px" }}
                >
                  <div
                    className=" d-flex flex-column"
                    style={{ width: "150px", margin: "auto", position: "relative" }}
                  >
                    
                      <img
                        src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-profiles/avatar-1.webp"
                        alt="Generic placeholder image"
                        className="img-fluid img-thumbnail mt-4 mb-2"
                        style={{ width: "150px", zIndex: "1" }}
                      />



                    
                  </div>
                  
                </div>


                {/* Upload Button */}
                <div style={{textAlign:"center"}}>
                
                <label className="image-upload-label">
                <i className="fa-solid fa-2xl fa-upload my-4" style={{color: "black" , zIndex:"34"}}></i>
                      <input
                        type="file"
                        id="fileInput"
                        accept="image/*"
                        onChange={handleImageUpload}
                        ref={fileInputRef}
                        style={{ display: "none" }}
                      />
                    </label>
                
                </div>

                {/* --- */}
      

                {/* -----------------------------------Name and Email */}
                <div className="p-3">
                  <h4>{Account.name}</h4>
                  <h5>{Account.email}</h5>
                </div>

                {/* ---------- */}

                <div className="card-body p-4 text-black">
                  {/* About Card */}
                  <div className="mb-5">
                    <p className="lead fw-normal mb-1">About</p>
                    <div
                      className="p-4 d-flex"
                      style={{ backgroundColor: "#f8f9fa" }}
                    >
                      <div className="container">
                        <div className="row">
                          <div className="col-md-4">
                          <input style={{width:"100%"  , border:"none" , borderRadius:"3px"}} type="text" onChange={hanldeOnChange} className="p-2 m-1" name="About1" placeholder="Something about You" />
                          </div>
                          <div className="col-md-4">
                          <input style={{width:"100%"  , border:"none" , borderRadius:"3px"}} type="text" onChange={hanldeOnChange} className="p-2 m-1" name="About2" placeholder="Something about You" />
                          </div>
                          <div className="col-md-4">
                            <input style={{width:"100%"  , border:"none" , borderRadius:"3px"}} type="text" onChange={hanldeOnChange} className="p-2 m-1" name="About3" placeholder="Something about You" />
                          </div>
                        </div>
                      </div>

                    </div>
                  </div>
                  {/* ---------- */}


                  {/* Create Blog Button */}
                  <div>
                    <button  className="w-100 btn btn-dark">Add Blogs</button>
                  </div>
                  {/* --------------------- */}

                  <div style={{ textAlign: "center" }} className="">
                    <button
                      onClick={handleOnUpdate}
                      type="button"
                      className="btn btn-outline-dark my-4"
                      data-mdb-ripple-color="dark"
                      style={{
                        zIndex: "1",
                        backgroundColor: "black",
                        color: "white",
                      }}
                    >
                      Update
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
