import React, { useEffect, useState } from 'react'

export default function ContectMessage() {
  const [data , setData ] = useState()
  const [counter , setCounter] = useState()




  // Fetching all the messages 
  useEffect(()=>{
    const getAllMessage = async()=>{
      const data = await fetch('http://127.0.0.1:8000/Blog/GetAllMessages' , {
        method:"GET"
      })

      const res = await data.json()
      console.log(res)
      if(res.status==="Success"){
        setData(res.data)
      }
    }
    getAllMessage()
  },[])

  
  return (
    <>
      <div className="container">
        {data && data.length > 0 ? data.map((data)=>{
          return <div className='border m-2 p-1' key={data._id} >
            <ul>
              <li style={{listStyle:"none"}} >Name : {data.name}</li>
              <li></li>
            </ul>
          </div>
        }):<h1>No Messages</h1>}
      </div>
    </>
  )
}
