import React, { useContext, useEffect, useState } from 'react';
import '../../css/CreatePost.css'
import { useLocation, useNavigate } from 'react-router-dom'; // Import useNavigate
import { InfoContext } from '../../Context/InfoProvider';
import axios from 'axios'
export default function CreatePost() {
// For Time

const initialState = {
  title: '',
  description: '',
  picture: {}, // Change from string to object
  username: '',
  categories: '',
  createdDate: '',
};

const [postData, setPostData] = useState(initialState);
const location = useLocation();
const { Account } = useContext(InfoContext);
const navigate = useNavigate();
const [file, setFile] = useState(); // Initialize with null instead of undefined



// ---------------Handle For change
const handleOnChange = (e) => {
  setPostData({ ...postData, [e.target.name]: e.target.value });
};



// This function will work when we  press the create button 
const handleOnSavePost = async (e) => {
  e.preventDefault();

  // Update postData fields
  postData.username = Account.name;
  postData.createdDate = new Date().toLocaleDateString();
  postData.categories = location.search?.split("=")[1] || "All";

  const token = sessionStorage.getItem('accessToken');

  // Create FormData and append both file and JSON data
  const formData = new FormData();
  formData.append('file', file);
  formData.append('postData', JSON.stringify(postData));

  try {
    // Make a single request for both file and JSON data
    const response = await axios.post('http://127.0.0.1:8000/Blog/createPost', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
        'Authorization': `${token}`,
      },
    });

    const res = response.data;
    console.log(res);

    if (response.msg === 'jwt expired') {
      alert('Please login again session expired');
      navigate('/login');
    }

    if (res.status === 'Success') {
      console.log('Done');
      navigate('/');
    }
  } catch (error) {
    console.error(error);
  }
};





// We creating here a function in which we get the image as soon as we addd it to the page.

useEffect(() => {
  const PostImg = async () => {
    if (file) {
      const token = sessionStorage.getItem('accessToken');

      const formData = new FormData();
      formData.append('file', file);

      try {
        const response = await axios.post(
          'http://localhost:8000/Blog/PostUploadImage',
          formData,
          {
            headers: {
              'Content-Type': 'multipart/form-data',
              Authorization: `${token}`,
            },
          }
        );

        const res = response.data;
        console.log(res);
        console.log('Page refreshed');
      } catch (error) {
        console.error(error);
      }
    }
  };

  PostImg();
}, [file]);



useEffect(()=>{
  const getImg = async()=>{
    const data = await axios.get('http://localhost:8000/Blog/GetImage')
    const res = await data.json()
    console.log(res.data)
  }
  getImg()
},[file])





// https://images.unsplash.com/uploads/141103282695035fa1380/95cdfeef?auto=format&fit=crop&q=80&w=2030&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D


// const imageUrl = postData.picture
// ? `http://localhost:8000/${postData.picture.replace(/\\/g, '/')}`
// : 'https://images.unsplash.com/uploads/141103282695035fa1380/95cdfeef?auto=format&fit=crop&q=80&w=2030&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D'

const imageUrl = 'https://images.unsplash.com/uploads/141103282695035fa1380/95cdfeef?auto=format&fit=crop&q=80&w=2030&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D'

  return (
    <>
    

      <div className="CreatePost container text-center bg-image-CreatePost">
        <img className='my-4' src={imageUrl} alt="Pic" />
      </div>

        <div className="container">
          <div className="row  my-2 p-2">
            <div className="col-md-1">
              <div className="my-1 p-1">
                <label style={{ cursor: 'pointer' }} htmlFor="fileinput">
                  <i className="fa-solid mx-2 fa-upload fa-2x" style={{ color: 'gray' }}></i>
                  <input
                    className="border border-primary"
                    id="fileinput"
                    type="file"
                    style={{ display: 'none' }}
                    onChange={(e)=>{setFile(e.target.files[0]) , console.log(e.target.files[0])}}
                  />

                  <button onClick={handleOnAddImage} className='btn btn-primary'>Add Image</button>


                </label>
              </div>
            </div>
            <div className="col-md-10">
              <div>
                <input
                  onChange={handleOnChange}
                  style={{ borderRadius: '0px', width: '100%' }}
                  name="title"
                  placeholder="Title..."
                  type="text"
                  id="typeText"
                  className="form-control"
                />
              </div>
            </div>
            <div className="my-2">
              <textarea
                onChange={handleOnChange}
                placeholder="Your Story here..."
                name="description"
                style={{ borderRadius: '0px', width: '100%' }}
                className="form-control bord"
                id="textAreaExample"
                rows="5"
              ></textarea>
            </div>
            <div className="col-md-1 my-1 px-2">
              <button onClick={handleOnSavePost} className="btn btn-primary my-1">Publish</button>
            </div>
          </div>
        </div>
    </>
  );
}
