import React, { useState } from 'react';
import { Link as RouterLink, useSearchParams } from 'react-router-dom';
import { Link as ScrollLink, animateScroll as scroll } from 'react-scroll';
import Post from '../Blogs/Post';
import '../../css/Categories.css';

export default function Categories() {
  const data = [
    { id: 1, type: 'All' },
    { id: 2, type: 'Music' },
    { id: 3, type: 'Movies' },
    { id: 4, type: 'Sports' },
    { id: 5, type: 'Tech' },
    { id: 6, type: 'Fashion' },
    { id: 7, type: 'Food' },
    { id: 8, type: 'Entertainment' },
    { id: 9, type: 'Games' },
  ];

  const [searchParams] = useSearchParams();
  const category = searchParams.get('category');

  const handleCategoryClick = (category) => {
    // You can add any additional logic here before changing the route
    // For example, you may want to update the state or perform other actions

    // Scroll to the "blogs" section
    scroll.scrollTo('blogs', {
      smooth: true,
      offset: -70,
      duration: 500,
    });

    // Change the route
    window.location.href = `/?category=${category}`;
  };

  return (
    <>
      <div style={{ textAlign: 'start' }} className="btnDiv container">
        <RouterLink to={`/create?category=${category || ''}`}>
          <button
            style={{
              fontSize: '20px',
              fontWeight: '600',
              letterSpacing: '3px',
              backgroundColor: '#2e2e2e',
              width: '100%',
              borderRadius: '3px',
              color: 'white',
              padding: '15px 0px',
            }}
            className="my-3 btn"
          >
            CREATE BLOG
          </button>
        </RouterLink>
      </div>

      {/* Table Component */}
      <div className="container-fluid">
        <div className="row">
          {/* Box-1 */}
          <div className="CategoryBox col-md-3">
            <div>
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th scope="col">
                      <div>
                        <RouterLink
                          to={'/'}
                          style={{
                            textDecoration: 'none',
                            color: 'black',
                          }}
                        >
                          <span style={{ fontSize: '30px' }}>All Categories</span>
                        </RouterLink>
                      </div>
                    </th>
                  </tr>
                </thead>
                <tbody>
                  {data.map((data) => (
                    <tr key={data.id}>
                      <th scope="row">
                        <ScrollLink
                          activeClass="active"
                          to="blogs"
                          spy={true}
                          smooth={true}
                          offset={-70}
                          duration={500}
                          style={{
                            textDecoration: 'none',
                            color: 'black',
                            display: 'block',
                          }}
                          onClick={() => handleCategoryClick(data.type)}
                        >
                          {data.type}
                        </ScrollLink>
                      </th>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
          {/* Box-1 */}

          {/* --------------------------------Box-2 */}
          <div id="blogs" className="col-md-9">
            <Post />
          </div>
        </div>
      </div>
    </>
  );
}
