import React, { useContext, useRef } from "react";
import { InfoContext } from "../Context/InfoProvider";
import '../css/EditProfile.css'

export default function MyProfile() {
  const { Account } = useContext(InfoContext);
  const fileInputRef = useRef(null);

  const handleOnUpdate = () => {
    console.log("Profile Updated");
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    // Handle the file upload, e.g., update the user's profile picture
    console.log("File uploaded:", file);
  };

  return (
    <>
      <section className="h-100 gradient-custom-2">
        <div className="container-fluid py-5 h-100">
          <div className="row d-flex justify-content-center align-items-center h-100">
            <div className="col col-lg-9 col-xl-7">
              <div className="card">
                <div
                  className="rounded-top text-white d-flex flex-row border border-danger"
                  style={{ backgroundColor: "#000", height: "200px" }}
                >
                  <div
                    className=" d-flex flex-column"
                    style={{ width: "150px", margin: "auto", position: "relative" }}
                  >
                    <label htmlFor="fileInput" className="image-upload-label">
                      <img
                        src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-profiles/avatar-1.webp"
                        alt="Generic placeholder image"
                        className="img-fluid img-thumbnail mt-4 mb-2"
                        style={{ width: "150px", zIndex: "1" }}
                      />
                      <input
                        type="file"
                        id="fileInput"
                        accept="image/*"
                        onChange={handleImageUpload}
                        ref={fileInputRef}
                        style={{ display: "none" }}
                      />
                    </label>

                    
                  </div>
                  
                </div>


                {/* Upload Button */}
                <div style={{textAlign:"center"}}>
                <i class="fa-solid fa-xl fa-upload my-4" style={{color: "black" , zIndex:"34"}}></i>
                </div>
                <div
                  className="p-4 text-black"
                  style={{ backgroundColor: "#f8f9fa" }}
                ></div>

                {/* -----------------------------------Name and Email */}
                <div className="p-3">
                  <h4>{Account.name}</h4>
                  <h5>{Account.email}</h5>
                </div>

                {/* ---------- */}

                <div className="card-body p-4 text-black">
                  {/* About Card */}
                  <div className="mb-5">
                    <p className="lead fw-normal mb-1">About</p>
                    <div
                      className="p-4 d-flex"
                      style={{ backgroundColor: "#f8f9fa" }}
                    >
                      <div className="container">
                        <div className="row">
                          <div className="col-md-4">
                            <p className="font-italic mx-3 mb-1">
                              Web Developer
                            </p>
                          </div>
                          <div className="col-md-4">
                            <p className="font-italic mx-3 mb-1">
                              Lives in New York
                            </p>
                          </div>
                          <div className="col-md-4">
                            <p className="font-italic mx-3 mb-1">
                              Photographer
                            </p>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                  {/* ---------- */}

                  <div style={{ textAlign: "center" }} className="">
                    <button
                      onClick={handleOnUpdate}
                      type="button"
                      className="btn btn-outline-dark"
                      data-mdb-ripple-color="dark"
                      style={{
                        zIndex: "1",
                        backgroundColor: "black",
                        color: "white",
                      }}
                    >
                      Update
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
