import React, { useEffect, useState } from 'react';
import PostsCard from './PostsCard';
import { useNavigate, useSearchParams, Link, useLocation } from 'react-router-dom';

export default function Post() {
  const [posts, setPosts] = useState([]);
  const [searchParams] = useSearchParams();
  const searchCategory = searchParams.get('category');
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const location = useLocation();  // Use useLocation outside of the function

  useEffect(() => {
    const token = sessionStorage.getItem('accessToken');

    setLoading(true);
    const fetchPosts = async (category) => {
      try {
        let url = 'https://blognewbackend.onrender.com/Blog/Post';

        if (category) {
          url = `https://blognewbackend.onrender.com/Blog/Post?category=${category}`;
        }

        const response = await fetch(url, {
          method: 'GET',
          headers: {
            Authorization: token,
          },
        });

        setLoading(false);
        const data = await response.json();

        if (data.status === 'Success') {
          setPosts(data.post);
        }
      } catch (error) {
        console.error(error);
      }
    }

    fetchPosts(searchCategory);
  }, [searchCategory]);

  const handleOnFilter = async (sortBy) => {
    try {
      const token = sessionStorage.getItem('accessToken');
      setLoading(true);

      let sortUrl = `https://blognewbackend.onrender.com/Blog/Filter/${sortBy}`;

      const res = await fetch(sortUrl, {
        method: 'GET',
        headers: {
          Authorization: token,
        },
      });

      const data = await res.json();
      setLoading(false);

      if (data.status === 'Success') {
        setPosts(data.data);
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <>
      <div style={{ textAlign: 'end' }} className="FilterBox p-3">
        <div className="dropdown">
          <button className="btn btn-secondary dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">
            Filter
          </button>
          <ul className="dropdown-menu">
            <li onClick={() => { handleOnFilter('asc') }} style={{ cursor: "pointer" }} className='p-2 border' >Newest</li>
            <li onClick={() => { handleOnFilter("desc") }} style={{ cursor: "pointer" }} className='p-2 border' >Oldest</li>
          </ul>
        </div>
      </div>

      {loading ? (
        <div style={{ textAlign: "center", fontSize: "50px", marginTop: "150px" }}>
          <i className="fa-solid fa-2xl fa-spinner fa-spin" style={{ color: "#111212" }}></i>
        </div>
      ) : posts.length > 0 ? (
        <div className="row">
          {posts.map((data, index) => (
            <div className="col-md-4" key={index}>
              <Link to={`postDetail/${data._id}`}>
                <PostsCard data={data} />
              </Link>
            </div>
          ))}
        </div>
      ) : (
        <div style={{ display: "flex", justifyContent: "center", textAlign: "start" }}>
          <h1 className='my-5'>No Current Blogs</h1>
        </div>
      )}
    </>
  );
}
