import React, { useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { InfoContext } from '../../Context/InfoProvider';
import axios from 'axios';

export default function CreatePost() {
  const initialState = {
    title: '',
    description: '',
    picture: 'https://images.unsplash.com/uploads/141103282695035fa1380/95cdfeef?q=80&w=2030&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', // Change from string to object
    username: '',
    categories: '',
    createdDate: '',
    pictureName:''
  };

  const [postData, setPostData] = useState(initialState);
  const { Account } = useContext(InfoContext);
  const navigate = useNavigate();
  const [file, setFile] = useState(null);
  const [loading , setloading] = useState(false)


  // ---------------Handle For change
  const handleOnChange = (e) => {
    setPostData({ ...postData, [e.target.name]: e.target.value });
  };

  // This function will work when we press the create button
  const handleOnSavePost = async (e) => {
    e.preventDefault();
  
    // Update postData fields
    postData.username = Account.name;
    postData.createdDate = new Date().toLocaleDateString();
    postData.categories = location.search?.split("=")[1] || "All";
  
    const token = sessionStorage.getItem('accessToken');
  
    // Create FormData and append both file and JSON data
    const formData = new FormData();
      formData.append('file', file);
    formData.append('postData', JSON.stringify(postData));
  
    // setloading(true)
    try {
      // Make a single request for both file and JSON data
      const response = await axios.post('http://127.0.0.1:8000/Blog/createPost', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Authorization': `${token}`,
        },
      });
  
      const res = response.data;
      console.log(res);
      // setloading(false)
      console.log(res);
  
      if (response.msg === 'jwt expired') {
        alert('Please log in again, session expired');
        navigate('/login');
      }
  
      if (res.status === 'Success') {
        console.log('Done');
        navigate('/');
      }
    } catch (error) {
      console.error(error.message);
    }
  };

  // Function to handle image upload
  const handleOnAddImage = (e) => {
    setFile(e.target.files[0]);
    // Show the uploaded image
    const imageUrl = URL.createObjectURL(e.target.files[0]);
    postData.picture = 'https://images.unsplash.com/uploads/141103282695035fa1380/95cdfeef?q=80&w=2030&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D'
    setPostData({ ...postData, picture: imageUrl });
  };


  return (
    <>
      <div className="CreatePost container text-center bg-image-CreatePost">
        <img className='my-4' src={postData.picture}  alt={postData.picture} />
      </div>

      <div className="container">
        <div className="row  my-2 p-2">
          <div className="col-md-1">
            <div className="my-1 p-1">
              <label style={{ cursor: 'pointer' }} htmlFor="fileinput">
                <i className="fa-solid mx-2 fa-upload fa-2x" style={{ color: 'gray' }}></i>
                <input
                  className="border border-primary"
                  id="fileinput"
                  type="file"
                  style={{ display: 'none' }}
                  onChange={handleOnAddImage}
                />
              </label>
            </div>
          </div>
          <div className="col-md-10">
            <div>
              <input
                onChange={handleOnChange}
                style={{ borderRadius: '0px', width: '100%' }}
                name="title"
                placeholder="Title..."
                type="text"
                id="typeText"
                className="form-control"
              />
            </div>
          </div>
          <div className="my-2">
            <textarea
              onChange={handleOnChange}
              placeholder="Your Story here..."
              name="description"
              style={{ borderRadius: '0px', width: '100%' }}
              className="form-control bord"
              id="textAreaExample"
              rows="5"
            ></textarea>
          </div>
          <div className="col-md-1 my-1 px-2">
          {loading ? <div style={{textAlign:"center"}}><i className="fa-solid fa-2xl fa-spinner fa-spin" style={{color: "#111212"}}></i></div> : <button   onClick={handleOnSavePost} type="submit" className="btn btn-primary ">Create</button> }
          </div>
        </div>
      </div>
    </>
  );
}
