      // navigate('/')
      // setIsUserAuth(true)

      // Storing the accessToken in sessionstorage
      // sessionStorage.setItem('accessToken' , `Bearer ${res.data.AccessToken}`)
      // sessionStorage.setItem('refreshToken' , `Bearer ${res.data.RefreshToken}`)

      // console.log(res.data.userName)
      // setAccount({email:res.data.email , name:res.data.userName})
      // console.log("This is account " + Account.name)/


    }

    // To handle errors
    if(res.status === 'Failed'){
      setLoginError(res.message)
    }

 
  }
  


  return (
    <>
      {/* Comments Section */}
      <div className="container-fluid border bg-light my-5 p-5">
        <div style={{ fontFamily: "Libre Baskerville" }} className="container">
          <h1>Comments</h1>
        </div>

        {/* ------------------------------------------------------Add Comment Form */}
        <div className="bg-light container">
          <div className="container">
            <h2 style={{ fontFamily: "Libre Baskerville" }}>Add Comment</h2>
          </div>

          {/* Add Comments */}
          <div className=" my-5">
            <form onSubmit={handleOnPostComment} >
              <div className="form-outline border-dark border-bottom p-2">

                <input onChange={handleOnChange} type="text" name="name" className="form-control" />

              </div>

              <div className="form-outline border-dark border-bottom p-2 my-5">
              <label className="form-label" >
                  Your Email
                </label>
                <input onChange={handleOnChange} type="email" name="email" className="form-control" />

              </div>

              <div className="form-outline border-dark border-bottom p-2 my-5">
              <label className="form-label" >
                  Comment{" "}
                </label>
                <textarea
                onChange={handleOnChange}
                  name="comment"
                  style={{ borderRadius: "0px", width: "100%" }}
                  className="form-control bord"
                  id="textAreaExample"
                  rows="5"
                ></textarea>

              </div>

              <button type="submit" className="btn btn-primary w-100 py-3">Submit</button>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}
