import React, { useContext, useEffect, useState } from 'react';
import '../../css/CreatePost.css'
import { useLocation, useNavigate } from 'react-router-dom'; // Import useNavigate
import { InfoContext } from '../../Context/InfoProvider';
import axios from 'axios'
export default function CreatePost() {
// For Time

const initialState = {
  title: '',
  description: '',
  picture: {}, // Change from string to object
  username: '',
  categories: '',
  createdDate: '',
};

const [postData, setPostData] = useState(initialState);
const location = useLocation();
const { Account } = useContext(InfoContext);
const navigate = useNavigate();
const [file, setFile] = useState(); // Initialize with null instead of undefined
const [img , setImg] = useState()


// -------------------------ImageUpload
// const handleOnSaveImage = async ()=>{
//   const token = sessionStorage.getItem('accessToken')

//   const formdata = new FormData()
//   formdata.append('file' , file)
//   console.log(file)
//   console.log(formdata)

//   try {
//     const data = await axios.post('http://127.0.0.1:8000/Blog/uploadImage' ,formdata ,{
//       headers: {
//         "Authorization": `${token}`,
//       },
//     })
//     console.log(data)
//   } catch (error) {
//     console.log(error)
//   }


// }




const handleOnSavePost = async (e) => {
  e.preventDefault();

  const token = sessionStorage.getItem('accessToken');

  const formdata = new FormData();
  formdata.append('file', file);

  // Attach the file to the FormData
  try {
    const fileResponse = await axios.post('http://127.0.0.1:8000/Blog/uploadImage', formdata, {
      headers: {
        "Content-Type": "multipart/form-data",
        "Authorization": `${token}`,
      },
    });

    const fileData = fileResponse.data;

    if (fileData.status !== 'Success') {
      console.error('Error uploading the file');
      return;
    }

    // File has been uploaded, now create the post
    const postDataWithoutPicture = { ...postData };
    delete postDataWithoutPicture.picture;

    const postResponse = await axios.post('http://127.0.0.1:8000/Blog/createPost', {
      ...postDataWithoutPicture,
      picture: fileData.fileName, // Assuming the backend sends the file name after successful upload
    }, {
      headers: {
        "Content-Type": "application/json",
        "Authorization": `${token}`,
      },
    });

    const postRes = postResponse.data;
    console.log(postRes);

    if (postResponse.msg === "jwt expired") {
      alert("Please login again session expired");
      navigate('/login');
    }

    if (postRes.status === "Success") {
      console.log("Done");
      navigate('/');
    }
  } catch (error) {
    console.error(error);
  }
};


const handleOnChange = (e) => {
  setPostData({ ...postData, [e.target.name]: e.target.value });
};




