import React, { useContext, useState } from 'react';
import '../css/LoginSignUp.css';
import { useNavigate } from 'react-router-dom';
import { InfoContext } from '../Context/InfoProvider';

export default function FormLogin({ setIsUserAuth }) {
  const [form, setForm] = useState({});
  const [showLogin, setShowLogin] = useState(true);
  const [errMsg, setErrMsg] = useState('');
  const [loading, setLoading] = useState(false); // Step 1: Loading state

  const handleShowLoginSignup = () => {
    setShowLogin(!showLogin);
  };

  const handleOnChange = (e) => {
    e.preventDefault();
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleOnSignUp = async (e) => {
    e.preventDefault();

    const data = await fetch('https://blogbackend-jpc6.onrender.com/Blog/SignUp', {
      method: 'POST',
      body: JSON.stringify(form),
      headers: {
        'Content-type': 'application/json',
      },
    });

    const res = await data.json();

    if (res.status === 'Success') {
      setShowLogin(true);
      setForm({});
      setErrMsg('');
    }

    if (res.status === 'Failed') {
      if (res.message.startsWith('user validation failed')) {
        setErrMsg('Please fill up all the inputs');
      } else if (res.message.startsWith('E11000 duplicate key error collection')) {
        setErrMsg('User with this Email already exists');
      } else {
        setErrMsg('There is something wrong');
      }
    }
  };

  const initialLoginInfo = {
    email: '',
    password: '',
  };

  const [loginInfo, setLoginInfo] = useState(initialLoginInfo);
  const [loginError, setLoginError] = useState('');

  const { Account, setAccount } = useContext(InfoContext);

  const navigate = useNavigate();

  const handleOnLoginChange = (e) => {
    setLoginInfo({ ...loginInfo, [e.target.name]: e.target.value });
  };

  const handleOnLogin = async (e) => {
    e.preventDefault();

    setLoading(true); // Step 2: Set loading to true when login starts

    const data = await fetch('https://blogbackend-jpc6.onrender.com/Blog/Login', {
      method: 'POST',
      body: JSON.stringify(loginInfo),
      headers: {
        'Content-type': 'application/json',
      },
    });

    const res = await data.json();

    if (res.status === 'Success') {
      setLoginError('');
      navigate('/');
      setIsUserAuth(true);

      sessionStorage.setItem('accessToken', `Bearer ${res.data.AccessToken}`);
      sessionStorage.setItem('refreshToken', `Bearer ${res.data.RefreshToken}`);

      setAccount({ email: res.data.email, name: res.data.userName });
    }

    if (res.status === 'Failed') {
      setLoginError(res.message);
    }

    setLoading(false); // Step 3: Set loading to false when login ends
  };

  return (
    <>
      {showLogin ? (
        <div className="container">
          {/* ... (Other code) */}
          {/* Submit button */}
          <button type="submit" className="btn btn-primary btn-lg btn-block" disabled={loading}>
            {loading ? 'Logging in...' : 'Login'}
          </button>
          {/* ... (Other code) */}
        </div>
      ) : (
        <div className="container">
          {/* ... (Other code) */}
          <div className="d-flex justify-content-center mx-4 mb-1 mb-lg-4">
            <button onClick={handleOnSignUp} type="button" className="btn btn-primary" disabled={loading}>
              {loading ? 'Signing up...' : 'Register'}
            </button>
          </div>
          {errMsg ? <p style={{ textAlign: 'center', color: 'red' }}>{errMsg}</p> : ''}
          <div style={{ textAlign: 'center', color: '#3B71D3' }} className="div">
            <hr />
            <button className="btn btn-primary" onClick={handleShowLoginSignup}>
              Already have an account?
            </button>
          </div>
          {/* ... (Other code) */}
        </div>
      )}
    </>
  );
}
