import React, {  useState } from 'react'
import '../css/LoginSignUp.css'

export default function FormLogin() {

    const [form , setForm] = useState({})
    const [showLogin , setShowLogin] = useState(false)


    // Creating a function which will handle the showlogin function
    const handleShowLoginSignup = ()=>{
      if(showLogin===false){
        setShowLogin(true)
      }else{
        setShowLogin(false)
      }
    }




    // Function to set the data 
    const handleOnChange = (e)=>{
        e.preventDefault()
        // console.log(e.target.name , e.target.value)
        setForm({
          ...form , [e.target.name] : e.target.value  
        })
    }

    const handleOnSignUp =async (e)=>{
        e.preventDefault()
        console.log(form)

    // ----------------------------------------------Now we will post the data

       const data = await fetch('http://127.0.0.1:8000/Users' , {
            method:'POST',
            body:JSON.stringify(form),
            headers:{
                "Content-type":"application/json"
            }

        })
        console.log(data)

        const res = await data.json()
        console.log(res)

    }

    // How can we get all the data

//     const handleToGetAllData = async()=>{

//         const AllUser = await fetch('http://127.0.0.1:8000/Users' , {
//             method:'GET'
//         })

//         const res = await AllUser.json()
//         setList(res.data.users)
//         console.log(res.data.users)

//     }

//     useEffect(()=>{
//         handleToGetAllData()
//     },[])



// const render = list.map((data,index)=>{
//     return <div key={index}>
//         <p>{data.username}</p>
//     </div>
// })

  return (
    <>
{showLogin ? <div className="container">


<section class="vh-100">
  <div class="container py-5 h-100">
    <div class="row d-flex align-items-center justify-content-center h-100">
      <div class="col-md-8 col-lg-7 col-xl-6">
        <img src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-login-form/draw2.svg"
          class="img-fluid" alt="Phone image"/>
      </div>
      <div class="col-md-7 col-lg-5 col-xl-5 offset-xl-1">
        <form>
          {/* <!-- Email input --> */}
          <div class="form-outline mb-4">
            <input type="email" id="form1Example13" class="form-control  border-bottom form-control-lg" />
            <label class="form-label" for="form1Example13">Email address</label>
          </div>

          {/* <!-- Password input --> */}
          <div class="form-outline mb-4">
            <input type="password" id="form1Example23" class="form-control border-bottom form-control-lg" />
            <label class="form-label " for="form1Example23">Password</label>
          </div>

          <div class="d-flex justify-content-around align-items-center mb-4">
            {/* <!-- Checkbox --> */}
            <div class="form-check">
              <input class="form-check-input" type="checkbox" value="" id="form1Example3" checked />
              <label class="form-check-label" for="form1Example3"> Remember me </label>
            </div>
            <a href="#!">Forgot password?</a>
          </div>

          {/* <!-- Submit button --> */}
          <button onClick={handleShowLoginSignup} type="submit" class="btn btn-primary btn-lg btn-block">Sign in</button>

          <div class="divider d-flex align-items-center my-4">
            <p class="text-center fw-bold mx-3 mb-0 text-muted">OR</p>
          </div>

          <a class="btn btn-primary btn-lg btn-block" style={{backgroundColor: '#3b5998'}} href="#!"
            role="button">
            <i class="fab fa-facebook-f me-2"></i>Continue with Facebook
          </a>
          <a class="btn btn-primary btn-lg btn-block" style={{backgroundColor: '#55acee'}} href="#!"
            role="button">
            <i class="fab fa-twitter me-2"></i>Continue with Twitter</a>

        </form>
      </div>
    </div>
  </div>
</section>
</div> : <div className="container">

{/* ----------------------------------------------------Signup starts here */}

<section class="vh-100" >
  <div class="container h-100">
    <div class="row d-flex justify-content-center align-items-center h-100">
      <div class="col-lg-12 col-xl-11">
        <div class="card text-black" >
          <div class="card-body p-md-5">
            <div class="row justify-content-center">
              <div class="col-md-10 col-lg-6 col-xl-5 order-2 order-lg-1">

                <p class="text-center h1 fw-bold mb-1 mx-1 mx-md-4 ">Sign up</p>

                <form class="mx-1 mx-md-4">

                  <div class="d-flex flex-row align-items-center mb-4">
                    <i class="fas fa-user fa-lg me-3 fa-fw"></i>
