import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import '../../css/PostDetail.css'

export default function PostDetail() {

    const {id} = useParams()
    console.log(id)

    const [post ,setPost ]= useState()

    useEffect(() => {
        const token = sessionStorage.getItem('accessToken');
    
       const getInfoWithid =  async ()=>{
        const data = await fetch(`http://127.0.0.1:8000/postDetail/${id}` , {
            method:"GET",
            headers:{
                Authorization:token
            }
        })
        
        const res = await data.json()
         setPost(res.info)
        console.log(res.info)

        if(res.status==='Success'){
          // setPost(res.info)
        }
    
        console.log(post)
       }
       getInfoWithid()
    
      }, [id]);
    


  return (
    <>
    <div className="p-2 my-4 container">
      <h1 style={{letterSpacing:"1px" , fontSize:"35px" , fontWeight:"900"}} className='text-center'>{}</h1>

      <div style={{display:"flex" , justifyContent:"space-around"}} className='container'>

        <p> <b></b></p>
        <p><b>  </b></p>
      </div>

    </div>


    <div className="container ">
      <img  style={{height:"300px" , objectFit:"cover"}} className="card-img-top PostDetailContainerImage" src="https://images.unsplash.com/photo-1586339393565-32161f258eac?auto=format&fit=crop&q=80&w=2070&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1pXzExfHx8fGVufDB8fHx8fA%3D%3D
      " alt="" />
    </div>
    </>
  )
}
