import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "../../css/PostDetail.css";
import WriteComment from './WriteComment'

export default function PostDetail() {
  const { id } = useParams();
  const [post, setPost] = useState(null); // Initialize post as null

  useEffect(() => {
    const token = sessionStorage.getItem("accessToken");

    const getInfoWithId = async () => {
      const data = await fetch(`http://127.0.0.1:8000/postDetail/${id}`, {
        method: "GET",
        headers: {
          Authorization: token,
        },
      });

      const res = await data.json();
      setPost(res.info);
      console.log(res.info);
    };

    getInfoWithId();
  }, [id]);

  return (
    <>
      <div className="p-2 my-4 container ">
        <h1 style={{textAlign:"center"}}>This Is A Standard Format Post.</h1>
        {post ? ( // Only render when post data is available
          <>
            <h1
              style={{
                letterSpacing: "1px",
                fontFamily: "Libre Baskerville",
                color:"black"
              }}
              className="text-center"
            >
              {post.title}
            </h1>

            <div style={{display:"flex" , justifyContent:"space-between"}} className="container   dateCategoryBox" >

              <div >
                <p><b> Date : </b> {post.createdAt}</p> 
                <p><b> Category:  </b> {post.categories} </p>
              </div>


              <div className="">
              <i className="fa-solid fa-trash fa-xl" style={{color: '#050505'}}></i>
              </div>


            </div>
          </>
        ) : null}
      </div>

      <div className="container">
        {post ? ( // Only render when post data is available
          <img
            style={{ height: "300px", objectFit: "cover" }}
            className="card-img-top PostDetailContainerImage"
            src="https://images.unsplash.com/photo-1586339393565-32161f258eac?auto=format&fit=crop&q=80&w=2070&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1pXzExfHx8fGVufDB8fHx8fA%3D%3D"
            alt=""
          />
        ) : null}
      </div>

      {/* Description */}
      <div className="container my-4">
        {post ? (
          <p
            style={{
              color: "gray",
              fontFamily: "Metrophobic",
              textAlign: "start",
            }}
            className="p-4"
          >
            {post.description}
          </p>
        ) : null}
      </div>

      {/* Image 2 */}
      <div className="container">
        {post ? ( // Only render when post data is available
          <img
            style={{ height: "300px", objectFit: "cover" }}
            className="card-img-top PostDetailContainerImage"
            src="https://images.unsplash.com/photo-1504805572947-34fad45aed93?auto=format&fit=crop&q=80&w=2070&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D"
            alt=""
          />
        ) : null}
      </div>

      {/* About the person who wrote the blog  */}
      <div className="container PostDetailContainer ">
        <hr />
        {post ? (
          <div className="container">
            <div className="row">
              <div className="col-md-2 card-img-top ProfileDiv">
                <img
                  src="https://mdbcdn.b-cdn.net/img/new/avatars/1.webp"
                  width={"80px"}
                  className=" rounded-circle shadow-4"
                  alt="Avatar"
                />
              </div>

              <div className="col-md-10">
                <h2 className="px-1 py-2 PostDetailUser">{post.username}</h2>
                <p className="px-1 PostDetailUserAbout">
                  Lorem ipsum dolor, sit amet consectetur adipisicing elit.
                  Aperiam tempore similique pariatur ullam, quaerat, vero cumque
                  deserunt accusantium obcaecati iure perferendis saepe placeat
                  assumenda veritatis. Ut aliquam illum nesciunt ullam impedit
                  minima vel reprehenderit fugiat .
                </p>

                <div
                  style={{ textAlign: "start" }}
                  className="container   SocialContainer"
                >
                  {/* <!-- Facebook --> */}
                  <i className="fab fa-facebook-f mx-2"></i>

                  {/* <!-- Twitter --> */}
                  <i className="fab fa-twitter mx-2"></i>

                  {/* <!-- Instagram --> */}
                  <i className="fab fa-instagram mx-2"></i>

                  {/* <!-- Linkedin --> */}
                  <i className="fab fa-linkedin-in mx-2"></i>

                  {/* <!-- Github --> */}
                  <i className="fab fa-github mx-2"></i>
                </div>
                <hr />
              </div>
            </div>
          </div>
        ) : null}
      </div>

      {/* ------------------------------------------------------- Comment Section ---------------*/}
      <WriteComment  />
    </>
  );
}
