import React, { useState } from "react";
import Comments from "./Comments";
import { useNavigate, useParams } from "react-router-dom";

export default function CommentSection() {
  const now = new Date();
  const dayNames = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
  const monthNames = [
    'Jan', 'Feb', 'Mar', 'Apr', 'May', 'June', 'July',
    'Aug', 'Sept', 'Oct', 'Nov', 'Dec'
  ];
  
  const day = dayNames[now.getDay()];
  const month = monthNames[now.getMonth()];
  const year = String(now.getFullYear()); // Get the last two digits of the year
  const hours = String(now.getHours() % 12 || 12).padStart(2, '0'); // Convert 24-hour to 12-hour format
  const minutes = String(now.getMinutes()).padStart(2, '0');
  const ampm = now.getHours() < 12 ? 'AM' : 'PM';
  
  const formattedDateTime = `${day}, ${month} ${now.getDate()}, ${year} ${hours}:${minutes} ${ampm}`;
 
  // ------------------------------------------------------------------------------------------------





  const initialComment = {
    name: "",
    email: "",
    comment: "",
    createdAt: {
      formattedDateTime: formattedDateTime,
    }
  };
  const [comment, setComment] = useState(initialComment);


  // This will set the initialComment 
  const handleOnCommentChange = (e) => {
    setComment({...comment , [e.target.name]:e.target.value })
    // console.log({...comment , [e.target.name]:e.target.value })
  };
  



  // Function to work when the login features get clicked
  const handleOnPostComment  = async(e)=>{
    e.preventDefault()

    const token = sessionStorage.getItem('accessToken')
    console.log("Comment")

    comment.createdAt = formattedDateTime

    const data = await fetch('http://127.0.0.1:8000/Post/Comments' , {
      method:'POST',
      body:JSON.stringify(comment),
      headers:{
      "Content-type":"application/json",
      "Authorization": `${token}`
      
      }

     })

    //  consoling the res data
    const res = await data.json()
    console.log(res)


    if(res.status==="Success"){
      console.log("Comment success")
      setComment(comment.name === '')
    }

 
  }

  


  return (
    <>
      {/* Comments Section */}
      <div className="container-fluid border bg-light my-5 p-5">
        <div style={{ fontFamily: "Libre Baskerville" }} className="container">
          <h1>Comments</h1>
          <Comments/>
        </div>

        {/* ------------------------------------------------------Add Comment Form */}
        <div className="bg-light container">
          <div className="container">
            <h2 style={{ fontFamily: "Libre Baskerville" }}>Add Comment</h2>
          </div>

          {/* Add Comments */}
          <div className=" my-5">
            <form onSubmit={handleOnPostComment} >
              <div className="form-outline border-dark border-bottom p-2">
              <label className="form-label" >
                  Username
                </label>
                <input onChange={handleOnCommentChange} type="text" name="name" className="form-control" />

              </div>

              <div className="form-outline border-dark border-bottom p-2 my-5">
              <label className="form-label" >
                  Your Email
                </label>
                <input onChange={handleOnCommentChange} type="email" name="email" className="form-control" />

              </div>

              <div className="form-outline border-dark border-bottom p-2 my-5">
              <label className="form-label" >
                  Comment{" "}
                </label>
                <textarea
                onChange={handleOnCommentChange}
                  name="comment"
                  style={{ borderRadius: "0px", width: "100%" }}
                  className="form-control bord"
                  id="textAreaExample"
                  rows="5"
                ></textarea>

              </div>

              <button type="submit" className="btn btn-primary w-100 py-3">Submit</button>

              
            </form>
          </div>
        </div>
      </div>
    </>
  );
}
