import React, { useEffect, useState } from 'react';
import PostsCard from './PostsCard';
import { useSearchParams } from 'react-router-dom';

export default function Post() {

  const [Post , setPost] = useState([])
  const [searchParams] = useSearchParams()
  const [category , setCategory] = useState('')
  const Searchcategory = searchParams.get('category')
  console.log(category)
  console.log(Searchcategory)



  useEffect(() => {

    const token = sessionStorage.getItem('accessToken');
    const getAllPosts = async()=>{


        const data = await fetch(`http://127.0.0.1:8000/AllPosts` , {
            method:'GET',
            headers:{
              "Authorization": `${token}`
            },
            query:true,
            params:true
        })
        const res = await data.json()
        if(res.status==="Success"){
          setPost(res.post)
        }
        console.log(res.post)
     
        // setPost
    }
    getAllPosts()
        
  }, []);
  

  useEffect(() => {

    const token = sessionStorage.getItem('accessToken');
    const getCategoryPosts = async()=>{


        const data = await fetch(`http://127.0.0.1:8000/Post?category=${Searchcategory}` , {
            method:'GET',
            headers:{
              "Authorization": `${token}`
            },
            query:true,
            params:true
        })
        const res = await data.json()
        if(res.status==="Success"){
          setPost(res.post)
        }
        console.log(res.post)
     
        // setPost
    }
    getCategoryPosts()
        
  }, [Searchcategory]);

  return (

    <>

    {
      Post && Post.length > 0 ? <div className="row">
      {Post.map((data,index)=>{
      return <div className="col-md-4" key={index}>
        <PostsCard data={data} />
      </div>
    })}
    </div> : <div style={{display:"flex" , justifyContent:"center" , textAlign:"start"}}> <h1 className='my-5'>No Current Blogs</h1> </div>
    }
    
     

    </>

  );
}
