import React, { useEffect, useState } from 'react';
import PostsCard from './PostsCard';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { Link } from 'react-router-dom';

export default function Post() {
  const [posts, setPosts] = useState([]);
  const [searchParams] = useSearchParams();
  const searchCategory = searchParams.get('category');
  const navigate = useNavigate()

  useEffect(() => {
    const token = sessionStorage.getItem('accessToken');

    const fetchPosts = async (category) => {
      try {
        let url = 'http://127.0.0.1:8000/Post';

        if (category) {
          url = `http://127.0.0.1:8000/Post?category=${category}`;
        }

        const response = await fetch(url, {
          method: 'GET',
          headers: {
            Authorization: token,
          },
        });

        const data = await response.json();
        console.log(data)

        if(data.msg==="jwt expired"){
          // alert("Please login again session expired")
          navigate('/login')
        }

        if (data.status === 'Success') {
          setPosts(data.post);
        }
      } catch (error) {
        console.error(error);
      }
    }

    fetchPosts(searchCategory); // Fetch posts based on the category in the URL or fetch all posts.

  }, [searchCategory]);

  return (
    <>
      {posts.length > 0 ? (
        <div className="row">
          {posts.map((data, index) => (
            <div className="col-md-4" key={index}>
              <Link to={`postDetail/${data._id}`}>
                <PostsCard data={data} /> 
              </Link>
            </div>
          ))}
        </div>
      ) : (
        <div style={{ display: "flex", justifyContent: "center", textAlign: "start" }}>
          <h1 className='my-5'>No Current Blogs</h1>
        </div>
      )}
    </>
  );
}
