import React, { useEffect, useState } from 'react'

export default function Users() {



    const [user , setUser] = useState()
    const [counter , setCounter] = useState(1)
    const [loading , setLoading] = useState(false)
    const [editForm , showEditForm] = useState(false)


    // ------------------ Fetching the data
    useEffect(() => {
      const getUser = async () => {
          try {
            setLoading(true)
              const data = await fetch('http://127.0.0.1:8000/Blog/GetAllUsersAdmin', {
                  method: "GET",
                  headers: {
                      "Content-type": "application/json",
                  }
              });
              const res = await data.json();
              setLoading(false)
              console.log(res.data);

              if(res.status==="Success"){
                setUser(res.data)
              }
          } catch (error) {
              console.log(error.message);
          }
      };
  
      getUser();
  }, [counter]);
  

  // ------------Function to delete 
  const handleOnDelete =  async(id)=>{
    console.log(id)
    const data = await fetch(`http://127.0.0.1:8000/Blog/DeleteUser/${id}` , {
      method:"DELETE"
    })

    const res = await data.json()
    if(res.status==="Success"){
      setCounter(counter+1)
    }
    console.log(res)
  }


  return (
    <>

    {editForm ? <EditForm showEditForm={showEditForm} /> : loading ?  <div style={{textAlign:"center" , fontSize:"60px" , marginTop:"50px"}}><i className="fa-solid fa-2xl fa-spinner fa-spin " style={{color: "#111212"}}></i></div> : user && user.length > 0 ? user.map((data) => (
  <div key={data._id}>
    <div className="container border border-dark  p-2 my-3">
      <div className="row">
        <div style={{borderRight:"solid gray 1px" , textAlign:"center"}} className="col-md-3">
          <h3>Name</h3> <br />
          <h5 style={{ wordWrap: "break-word" }}>{data.firstname}</h5>
        </div>

        <div style={{borderRight:"solid gray 1px" , textAlign:"center"}} className="col-md-3">
          <h3>Email</h3> <br />
          <p style={{ wordWrap: "break-word" }}> {data.email}</p>
        </div>

        <div className="col-md-3" style={{borderRight:"solid gray 1px" , textAlign:"center"}}>
          <h3>About</h3> <br />
          <p style={{ wordWrap: "break-word" }}>{data.about ? data.about.split(' ').slice(0, 10).join(' ') : ''}</p>
        </div>

        <div className="col-md-3" style={{textAlign:"center"}}>
            <button onClick={()=>{handleOnDelete(data._id)}} className='btn btn-dark mx-1 my-5' >Delete</button>
            <button onClick={()=>{showEditForm(true)}} className='btn btn-dark mx-1 my-5'> Edit</button>
        </div>


      </div>
    </div>
  </div>
)) : <h1 style={{textAlign:"center"}} >No Current Users</h1>} 






    </>
  )
}



// ------------Edit Form-------
const EditForm = ({showEditForm})=>{
    const initialState = {
    name:"",
    email:"",
    about:""
  }

  const [userInfo , setUserInfo] = useState(initialState)

  const handleOnChange = (e)=>{
    setUserInfo({...userInfo , [e.target.name]:e.target.value})
  }

  const handleOnUpdate = (e)=>{
    e.preventDefault()
    console.log(userInfo)
  }

  return (
    <>
     
    <div> <span><i class="fa-solid fa-arrow-left fa-3x" onClick={()=>{showEditForm(false)}} style={{color:"#000000"}}></i></span><h1 style={{textAlign:"center"}} >Update Information</h1></div>

    <div style={{textAlign:"center"}} className="container w-75 border p-2" >
    <form onSubmit={handleOnUpdate} >
      <div><input onChange={handleOnChange} type="text" className='w-100 my-2 p-1' placeholder='Name' name='name' /></div>
      <div><input onChange={handleOnChange} type="text" className='w-100 my-2 p-2' placeholder='Email' name='email' /></div>
      <div><textarea onChange={handleOnChange} placeholder='About' className='w-100 my-2' name="about" ></textarea></div>
      
      <div>
        <button onClick={handleOnUpdate} className='btn btn-dark' >Update</button>
      </div>
    </form>
    </div>
    </>
  )
}
