import React, { useContext, useEffect, useState } from 'react'
import '../css/CreatePost.css'
import { useLocation } from 'react-router-dom'
import { InfoContext } from '../Context/InfoProvider'


export default function CreatePost() {
       // InitialState
       const InitialState = {
        title:'',
        description:'',
        picture:'',
        username:'',
        categories:'',
        createdDate:new Date()
    }

    const [postData , setPostData] = useState(InitialState)
    const [file , setFile] = useState('')
    const location = useLocation()
    const {Account} = useContext(InfoContext)



    useEffect(()=>{
        const getImage =  async(file)=>{

            if(file){
                const data = new FormData()
                data.append("name" , file.name)
                data.append("file" , file)
    
                // API CALL
                const response = await fetch('http://127.0.0.1:8000/file/upload' , {
                    method:'POST',
                    body:JSON.stringify(file),
                    headers:{
                        "Accept": "application/json, form-data", 
                        "Content-Type": "application/json"
                    }
                })
        
                // Here we are awaitng the res which we are getting from backend and we are the one who is sending it.
                const res = await response.json()
                console.log('res')
                console.log(res)
    
    
                postData.picture = ''
            }
        }
        getImage()
        postData.categories = location.search?.split("=")[1] || "All"
        // console.log(postData.categories)
        postData.username = Account.name
        // console.log(Account.name)
    } , []) 
    


    const handleOnChange = (e)=>{
        console.log({...postData , [e.target.name]:e.target.value})
        setPostData({...postData , [e.target.name]:e.target.value})
    }

    const imageUrl =  'https://images.unsplash.com/uploads/141103282695035fa1380/95cdfeef?auto=format&fit=crop&q=80&w=2030&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D'




    return (
        <>
        <div className=" container text-center bg-image-CreatePost " >
          <img src={imageUrl} alt="" />
      </div>
    
    {/* ---------------------------------------------Upload Button------------------------------------------------- */}
    
    
    
    <form>
    
    <div className="container">
    
    <div className="row  my-2 p-2">
        <div className="col-md-1">
        <div className='my-1 p-1'>
        <label style={{cursor:"pointer"}} htmlFor="fileinput">
        <i className="fa-solid mx-2 fa-upload fa-2x" style={{color: "gray"}}></i>
        <input className='border border-primary' onChange={(e)=>{setFile(e.target.files[1])}} id='fileinput' type="file" style={{display:"none"}} />
        </label>
    </div>
        </div>
    
        <div className="col-md-10">
    
        <div >
      <input onChange={handleOnChange} style={{borderRadius:"0px" , width:"100%" }} name='title' placeholder='Title...' type="text" id="typeText" className="form-control" />
    </div>
    
        </div>
    
        <div className=" my-2">
      <textarea onChange={handleOnChange} placeholder='Your Story here...' name='description' style={{borderRadius:"0px" , width:"100%" }} className="form-control bord" id="textAreaExample" rows="5"></textarea>
    </div>
    
        <div  className="col-md-1 my-1 px-2">
           <button  className='btn btn-primary my-1'>Publish</button>
        </div>
     
    </div>
    
    </div>
    
    
    
    
    </form>
    
    
    </>
      )
}
