export default function Comments() {
    const [post , setPost ] = useState()

    // To Get All the  comments 

    useEffect(()=>{
        const getComments = async()=>{

            const token = sessionStorage.getItem('accessToken')

            const data = await fetch('http://127.0.0.1:8000/Comments' , {
                method:"GET",
                headers:{
                    "Authorization": `${token}`
                }
            })

            const res = await data.json()
            console.log(res)

            if(res.status==="Success"){
                setPost(res.comments)
                console.log(post)
            }
            

        }
        getComments()
         // Rendering the comments here 
    const render = res.comments.map((data)=>{
        return <div key={data.id}>
<div className="container">
        {post && post.length > 0 ? (
          <div className="container">
            <div className="row">
              <div className="col-md-2 card-img-top ProfileDiv">
                <img
                  src="https://mdbcdn.b-cdn.net/img/new/avatars/1.webp"
                  width={"80px"}
                  className=" rounded-circle shadow-4"
                  alt="Avatar"
                />
              </div>

              <div className="col-md-10">
                <h2 className="px-1 py-2 PostDetailUser">{data.username}</h2>
                <p className="px-1 PostDetailUserAbout">
                  Lorem ipsum dolor, sit amet consectetur adipisicing elit.

                </p>

              </div>
            </div>
          </div>
        ) : <h2>No Comments</h2>}
      </div>
        </div>
    })
    } ,[])



  

  return (
    <>
      
    </>
  )
}
