import React, { useEffect, useState } from 'react'

export default function Users() {
    const [user , setUser] = useState()

    // ------------------ Fetching the data
    useEffect(() => {
      const getUser = async () => {
          try {
              const data = await fetch('http://127.0.0.1:8000/Blog/GetAllUsersAdmin', {
                  method: "GET",
                  headers: {
                      "Content-type": "application/json",
                  }
              });
              const res = await data.json();
              console.log(res.data);

              if(res.status==="Success"){
                setUser(res.data)
              }
          } catch (error) {
              console.log(error.message);
          }
      };
  
      getUser();
  }, []);
  


  return (
    <>

{user ? user.map((data) => (
  <div key={data._id}>
    <div className="container border  p-1 my-2">
      <div className="row">
        <div className="col-md-3 ">
          <h5>Name</h5> <br />
          {data.firstname}
        </div>

        <div className="col-md-3">
          <h5>Email</h5> <br />
          {data.email}
        </div>

        <div className="col-md-3">
          <h5>About</h5> <br />
          {data.about ? data.about.split(' ').slice(0, 10).join(' ') : ''}
        </div>

        <div className="col-md-3 border">
          <div style={{display:"flex" , justifyContent:"center" , textAlign:"center"}} >
            <button className='btn btn-dark mx-1' >Delete</button>
            <button className='btn btn-dark mx-1' >Edit</button>
            </div>
        </div>


      </div>
    </div>
  </div>
)) : null}


    </>
  )
}
