import React, { useContext, useState , useEffect } from 'react';
import '../../css/CreatePost.css'
import { useLocation, useNavigate } from 'react-router-dom'; // Import useNavigate
import { InfoContext } from '../../Context/InfoProvider';
import { useParams } from 'react-router-dom';
import axios from 'axios';

export default function Update() {

    const handleOnChange = (e) => {
        setPostData({ ...postData, [e.target.name]: e.target.value });
      };
    
      const imageUrl =
        'https://images.unsplash.com/uploads/141103282695035fa1380/95cdfeef?auto=format&fit=crop&q=80&w=2030&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D';
// For Time
  const InitialState = {
    title: '',
    description: '',
    picture: '',
    username: '',
    categories: '',
    createdDate: '',
  };

  const [postData, setPostData] = useState(InitialState);
  const location = useLocation();
  const { Account } = useContext(InfoContext);
  const navigate = useNavigate(); // Initialize useNavigate
  const {id} = useParams()
  const [file, setFile] = useState(null);


  useEffect(() => {
    const token = sessionStorage.getItem("accessToken");

    const getInfoWithId = async () => {
      const data = await fetch(`http://127.0.0.1:8000/Blog/postDetail/${id}`, {
        method: "GET",
        headers: {
          Authorization: token,
        },
      });

      const res = await data.json();
      setPostData(res.info);
      console.log(res.info)
    //   console.log(res.info);
    };

    getInfoWithId();
  }, [id]);


// Function will work as soon as we click on the update button 

const handleOnUpdatePost = async (e) => {
  e.preventDefault();

  // Update postData fields
  postData.username = Account.name;
  postData.createdDate = new Date().toLocaleDateString();
  postData.categories = location.search?.split("=")[1] || "All";

  const token = sessionStorage.getItem('accessToken');

  // Create FormData and append both file and JSON data
  const formData = new FormData();
  formData.append('file', file);
  formData.append('postData', JSON.stringify(postData));


  try {
    const response = await axios.put(
      `http://127.0.0.1:8000/Blog/UpdatePost/${id}`,
      formData,  // Pass the FormData as the body
      {
        headers: {
          'Content-Type': 'multipart/form-data', // Set Content-Type to multipart/form-data
          Authorization: `${token}`,
        },
      }
    );

    if (response.status === 200) {
      console.log("Done");
      navigate(`/postDetail/${id}`);
    }
  } catch (error) {
    console.error("Error updating post:", error);
  }
};



    const handleOnAddImage = (e) => {
    setFile(e.target.files[0]);
    // Show the uploaded image
    console.log(e.target.files[0])
    const imageUrl = URL.createObjectURL(e.target.files[0]);
    postData.picture = 'https://images.unsplash.com/uploads/141103282695035fa1380/95cdfeef?q=80&w=2030&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D'
    setPostData({ ...postData, picture: imageUrl });
  };




  return (
    <>
      <div className="CreatePost container text-center bg-image-CreatePost">
        <img className='my-2'  src={postData.picture} alt={postData.picture} />
      </div>

      <form>
        <div className="container">
          <div className="row  my-2 p-2">
            <div className="col-md-1">
              <div className="my-1 p-1">
                <label style={{ cursor: 'pointer' }} htmlFor="fileinput">
                  <i className="fa-solid mx-2 fa-upload fa-2x" style={{ color: 'gray' }}></i>
                  <input
                    className="border border-primary"
                    id="fileinput"
                    type="file"
                    style={{ display: 'none' }}
                    onChange={handleOnAddImage}
                  />
                </label>
              </div>
            </div>
            <div className="col-md-10">
              <div>
                <input
                  onChange={handleOnChange}
                  style={{ borderRadius: '0px', width: '100%' }}
                  name="title"
                  placeholder="Title..."
                  type="text"
                  id="typeText"
                  className="form-control"
                  value={postData.title || ''}
                />
              </div>
            </div>
            <div className="my-2">
              <textarea
                onChange={handleOnChange}
                placeholder="Your Story here..."
                name="description"
                style={{ borderRadius: '0px', width: '100%' }}
                className="form-control bord"
                id="textAreaExample"
                rows="5"
                value={postData.description || ''}
              ></textarea>
            </div>
            <div className="col-md-1 my-1 px-2">
              <button onClick={handleOnUpdatePost} className="btn btn-primary my-1">Update</button>
            </div>
          </div>
        </div>
      </form>
    </>
  );
}
