import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios'; // Import Axios
import '../css/CreatePost.css';
import { useLocation } from 'react-router-dom';
import { InfoContext } from '../Context/InfoProvider';

export default function CreatePost() {
  const InitialState = {
    title: '',
    description: '',
    picture: '',
    username: '',
    categories: '',
    createdDate: new Date(),
  };

  const [postData, setPostData] = useState(InitialState);
  const [file, setFile] = useState(null); // Initialize with null
  const location = useLocation();
  const { Account } = useContext(InfoContext);

  useEffect(() => {
    const getImage = async () => {
      if (file) {
        const data = new FormData();
        data.append('name', file.name);
        data.append('file', file);

        console.log(file)

        try {
          // Use Axios to make the API call
          const response = await axios.post('http://127.0.0.1:8000/file/upload', data, {
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'multipart/form-data', // Use 'multipart/form-data' for file uploads
            },
          });

          if (response.status === 200) {
            const res = response.data;
            console.log('Response from the backend:', res);

            // Update the state with the picture URL
            setPostData((prevData) => ({
              ...prevData,
              picture: res.imageUrl, // Replace 'imageUrl' with the actual response property
            }));

          } else {
            console.error('Failed to upload the file');
          }
        } catch (error) {
          console.error('Error uploading file:', error);
        }
      }
    };

    if (file) {
      getImage();
    }

    // Update other properties in the state
    setPostData((prevData) => ({
      ...prevData,
      categories: location.search?.split('=')[1] || 'All',
      username: Account.name,
    }));
  }, [file, location.search, Account.name]);

  const handleOnChange = (e) => {
    setPostData({ ...postData, [e.target.name]: e.target.value });
  };

  const imageUrl = postData.picture

  return (
    <>
      <div className=" container text-center bg-image-CreatePost">
        <img src={imageUrl} alt="" />
      </div>

      <form>
        <div className="container">
          <div className="row  my-2 p-2">
            <div className="col-md-1">
              <div className="my-1 p-1">
                <label style={{ cursor: 'pointer' }} htmlFor="fileinput">
                  <i className="fa-solid mx-2 fa-upload fa-2x" style={{ color: 'gray' }}></i>
                  <input
                    className="border border-primary"
                    onChange={(e) => setFile(e.target.files[0])}
                    id="fileinput"
                    type="file"
                    style={{ display: 'none' }}
                  />
                </label>
              </div>
            </div>
            <div className="col-md-10">
              <div>
                <input
                  onChange={handleOnChange}
                  style={{ borderRadius: '0px', width: '100%' }}
                  name="title"
                  placeholder="Title..."
                  type="text"
                  id="typeText"
                  className="form-control"
                />
              </div>
            </div>
            <div className="my-2">
              <textarea
                onChange={handleOnChange}
                placeholder="Your Story here..."
                name="description"
                style={{ borderRadius: '0px', width: '100%' }}
                className="form-control bord"
                id="textAreaExample"
                rows="5"
              ></textarea>
            </div>
            <div className="col-md-1 my-1 px-2">
              <button className="btn btn-primary my-1">Publish</button>
            </div>
          </div>
        </div>
      </form>
    </>
  );
}
