import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '../../css/PostDetail.css';

export default function PostDetail() {
  const { id } = useParams();
  const [post, setPost] = useState(null);

  useEffect(() => {
    const token = sessionStorage.getItem('accessToken');

    const getInfoWithId = async () => {
      const data = await fetch(`http://127.0.0.1:8000/postDetail/${id}`, {
        method: 'GET',
        headers: {
          Authorization: token,
        },
      });

      const res = await data.json();
      setPost(res.info);
    };

    getInfoWithId();
  }, [id]);

  return (
    <div className="post-detail-container">
      {post ? (
        <div>
          <h1 className="post-title">{post.title}</h1>
          <div className="post-metadata">
            <p className="post-date">{post.createdAt}</p>
            <p className="post-category">{post.categories}</p>
          </div>
          <div className="post-image-container">
            <img
              className="post-image"
              src="https://images.unsplash.com/photo-1586339393565-32161f258eac?auto=format&fit=crop&q=80&w=2070&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1pXzExfHx8fGVufDB8fHx8fA%3D%3D"
              alt=""
            />
          </div>
          <p className="post-description">{post.description}</p>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}
