import React, { useContext, useEffect, useRef, useState } from "react";
import { InfoContext } from "../Context/InfoProvider";
import '../css/EditProfile.css';
import { useNavigate } from "react-router-dom";

export default function MyProfile() {
  const navigate = useNavigate()

  const initialData = {
    About1: "",
    About2: "",
    About3: "",
    name:'',
    email:''
  };

  const { Account } = useContext(InfoContext);
  const fileInputRef = useRef(null);
  const [aboutInfo , setAboutInfo] = useState()


  const [data, setData] = useState(initialData);

  const handleOnUpdate = async () => {
    const token = sessionStorage.getItem('accessToken');
    console.log(data)

    data.name = Account.name
    data.email = Account.email


    try {
      const res = await fetch("http://127.0.0.1:8000/Blog/UpdateProfile", {
        method: "POST",
        body: JSON.stringify(data),
        headers: {
          "Content-type": "application/json",
          'Authorization': `${token}`,
        },
      });

      const info = await res.json();
      console.log(info);

      if(info.status==="Success"){
        navigate('/Profile')
      }

      console.log(aboutInfo)

    } catch (error) {
      console.log(error.message);
    }
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    // Handle the file upload, e.g., update the user's profile picture
    console.log("File uploaded:", file);
  };

  const hanldeOnChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };


  // To get the profile
  useEffect(()=>{
    const getProfile = async(name)=>{
      const token = sessionStorage.getItem('accessToken');
      const res = await fetch(`http://127.0.0.1:8000/Blog/GetProfile/${Account.email}` , {
        method:"GET",
        headers: {
          'Authorization': `${token}`,
        },
      })

      const data = await res.json()

      console.log(data)
      setAboutInfo(data.data)
    }
    getProfile()
  },[])

  return (
    <>
      <section className="h-100 gradient-custom-2">
        <div className="container-fluid py-5 h-100">
          <div className="row d-flex justify-content-center align-items-center h-100">
            <div className="col col-lg-9 col-xl-7">
              <form>
                <div className="card">
                  <div
                    className="rounded-top text-white d-flex flex-row border border-danger"
                    style={{ backgroundColor: "#000", height: "200px" }}
                  >
                    <div
                      className=" d-flex flex-column"
                      style={{ width: "150px", margin: "auto", position: "relative" }}
                    >
                      <img
                        src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-profiles/avatar-1.webp"
                        alt="Generic placeholder image"
                        className="img-fluid img-thumbnail mt-4 mb-2"
                        style={{ width: "150px", zIndex: "1" }}
                      />
                    </div>
                  </div>

                  {/* Upload Button */}
                  <div style={{ textAlign: "center" }}>
                    <label className="image-upload-label">
                      <i className="fa-solid fa-2xl fa-upload my-4" style={{ color: "black", zIndex: "34" }}></i>
                      <input
                        type="file"
                        id="fileInput"
                        accept="image/*"
                        onChange={handleImageUpload}
                        ref={fileInputRef}
                        style={{ display: "none" }}
                      />
                    </label>
                  </div>

                  {/* Name and Email */}
                  <div className="p-3">
                    <h4>{Account.name}</h4>
                    <h5>{Account.email}</h5>
                  </div>

                  <div className="card-body p-4 text-black">
                    {/* About Card */}
                    <div className="mb-5">
                      <p className="lead fw-normal mb-1">About</p>
                      <div
                        className="p-4 d-flex"
                        style={{ backgroundColor: "#f8f9fa" }}
                      >
                        <div className="container">
                          <div className="row">
                            <div className="col-md-4">
                              <input
                                style={{ width: "100%", border: "none", borderRadius: "3px" }}
                                type="text"
                                onChange={hanldeOnChange}
                                className="p-2 m-1"
                                name="About1"
                                placeholder="Something about You...."
                                // value={aboutInfo.About1 || ""}
                              />
                            </div>
                            <div className="col-md-4">
                              <input
                                style={{ width: "100%", border: "none", borderRadius: "3px" }}
                                type="text"
                                onChange={hanldeOnChange}
                                className="p-2 m-1"
                                name="About2"
                                placeholder="Something about You...."
                                // value={aboutInfo.About2 || ""}
                              />
                            </div>
                            <div className="col-md-4">
                              <input
                                style={{ width: "100%", border: "none", borderRadius: "3px" }}
                                type="text"
                                onChange={hanldeOnChange}
                                className="p-2 m-1"
                                name="About3"
                                placeholder="Something about You...."
                                // value={aboutInfo.About3 || ""}
                              />
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>

                    <div style={{ textAlign: "center" }} className="">
                      <button
                        onClick={handleOnUpdate}
                        type="button"
                        className="btn btn-outline-dark my-4"
                        data-mdb-ripple-color="dark"
                        style={{
                          zIndex: "1",
                          backgroundColor: "black",
                          color: "white",
                        }}
                      >
                        Update
                      </button>
                    </div>

                    {/* Create Blog Button */}
                    <div>
                      <button className="w-100 btn btn-dark">Add Blogs</button>
                    </div>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
