import React, {  useContext, useState } from 'react'
import '../css/LoginSignUp.css'
import { useNavigate } from 'react-router-dom'
import { InfoContext } from '../Context/InfoProvider'

export default function FormLogin({setIsUserAuth}) {

    const [form , setForm] = useState({})
    const [showLogin , setShowLogin] = useState(true)
    const [errMsg , seterrMsg] = useState('') 


    // Creating a function which will handle the showlogin function
    const handleShowLoginSignup = ()=>{
      if(showLogin===false){
        setShowLogin(true)
      }else{
        setShowLogin(false)
      }
    }




    // Function to set the data 
    const handleOnChange = (e)=>{
        e.preventDefault()
        // console.log(e.target.name , e.target.value)
        setForm({
          ...form , [e.target.name] : e.target.value  
        })
    }



    // ---------------------------------------------Function work whenver we click on signup
    const handleOnSignUp =async (e)=>{
        e.preventDefault()
        // console.log(form)

    // Now we will post the data

       const data = await fetch('https://blogbackend-jpc6.onrender.com/Blog/SignUp' , {
            method:'POST',
            body:JSON.stringify(form),
            headers:{
                "Content-type":"application/json"
            }

        })


        // Here we are awaitng the res which we are getting from backend and we are the one who is sending it.
        const res = await data.json()
        console.log('res')
        console.log(res)


        // This condition will work when the res will get success
        if(res.status==='Success'){
          setShowLogin(true)
          setForm({})
          seterrMsg('')
        }


        // Here we are setting up all the errors according to response from the backend.

        if(res.status==='Failed'){
          if (res.message.startsWith("user validation failed")) {
            seterrMsg("Please fill up all the inputs");
          }else if(res.message.startsWith("E11000 duplicate key error collection")){
            seterrMsg("User with this Email already exist")
          }else{
            seterrMsg("There is something wrong ")
          }
        }


    }



    // ----------------------------------------------------LOGIN-----------------------------------------------------------------------
    const initialLoginInfo = {
      email:'',
      password:''
    }
    
    const [loginInfo , setLoginInfo] = useState(initialLoginInfo)
    const [loginError , setLoginError] = useState('')

    const { Account , setAccount} = useContext(InfoContext)

    const navigate = useNavigate()


    // Function to work when the input fields change
    const handleOnLoginChange = (e)=>{
      setLoginInfo({...loginInfo , [e.target.name]:e.target.value})
      // console.log({...loginInfo , [e.target.name]:e.target.value})
    }


    // Function to work when the login features get clicked
    const handleOnLogin  = async(e)=>{
      e.preventDefault()


      const data = await fetch('https://blogbackend-jpc6.onrender.com/Blog/Login' , {
        method:'POST',
        body:JSON.stringify(loginInfo),
        headers:{
        "Content-type":"application/json"
        }

       })

      //  consoling the res data
      const res = await data.json()

      // If the req go success
      if(res.status==='Success'){
        setLoginError('')
        navigate('/')
        setIsUserAuth(true)

        // Storing the accessToken in sessionstorage
        sessionStorage.setItem('accessToken' , `Bearer ${res.data.AccessToken}`)
        sessionStorage.setItem('refreshToken' , `Bearer ${res.data.RefreshToken}`)

        // console.log(res.data.userName)
        setAccount({email:res.data.email , name:res.data.userName})
        // console.log("This is account " + Account.name)/

 
      }

      // To handle errors
      if(res.status === 'Failed'){
        setLoginError(res.message)
      }

   
    }
    

  return (
    <>



{showLogin ? <div className="container">


<section className="vh-100">
  <div className="container py-5 h-100">
    <div className="row d-flex align-items-center justify-content-center h-100">
      <div className="col-md-8 col-lg-7 col-xl-6">
        <img src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-login-form/draw2.svg"
          className="img-fluid" alt="Phone image"/>
      </div>
      <div className="col-md-7 col-lg-5 col-xl-5 offset-xl-1">
        <h1 style={{textAlign:"center"}}>Login</h1>
        {/* OnLogin */}
        <form onSubmit={handleOnLogin}>
          {/* <!-- Email input --> */}
          <div className="d-flex flex-row align-items-center mb-4">
    <i className="fas fa-envelope fa-lg me-3 fa-fw"></i>
    <input onChange={handleOnLoginChange} type="text" id="form3Example1c" name='email' placeholder="Your Email" className="form-control " />
  </div>

          {/* <!-- Password input --> */}
          <div className="d-flex flex-row align-items-center mb-4">
