import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios'; // Import Axios
import '../css/CreatePost.css';
import { useLocation } from 'react-router-dom';
import { InfoContext } from '../Context/InfoProvider';

export default function CreatePost() {
  const InitialState = {
    title: '',
    description: '',
    picture: '',
    username: '',
    categories: '',
    createdDate: new Date(),
  };

  const [postData, setPostData] = useState(InitialState);
  const location = useLocation();
  const { Account } = useContext(InfoContext);

  

  const handleOnSavePost = async (e) => {
    e.preventDefault();
    setPostData({
      ...postData,
      categories: location.search?.split("=")[1] || "All",
      username: Account.name,
    });
  
    try {
      const response = await fetch('http://127.0.0.1:8000/CreatePost', {
        method: 'POST',
        body: JSON.stringify(postData), // Convert postData to JSON
        headers: {
          'Content-Type': 'application/json',
        }
      });
  
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
  
      const data = await response.json();
      console.log('Response:', data);
    } catch (error) {
      console.error('Error:', error);
    }
  };


  const handleOnChange = (e) => {
    setPostData({ ...postData, [e.target.name]: e.target.value });
    console.log({ ...postData, [e.target.name]: e.target.value })
    // console.log(location.search?.split("=")[1]|| "All")
  };

  const imageUrl = 
    'https://images.unsplash.com/uploads/141103282695035fa1380/95cdfeef?auto=format&fit=crop&q=80&w=2030&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D';

  return (
    <>
      <div className=" container text-center bg-image-CreatePost">
        <img src={imageUrl} alt="" />
      </div>

      <form>
        <div className="container">
          <div className="row  my-2 p-2">
            <div className="col-md-1">
              <div className="my-1 p-1">
                <label style={{ cursor: 'pointer' }} htmlFor="fileinput">
                  <i className="fa-solid mx-2 fa-upload fa-2x" style={{ color: 'gray' }}></i>
                  <input
                    className="border border-primary"
                    id="fileinput"
                    type="file"
                    style={{ display: 'none' }}
                  />
                </label>
              </div>
            </div>
            <div className="col-md-10">
              <div>
                <input
                  onChange={handleOnChange}
                  style={{ borderRadius: '0px', width: '100%' }}
                  name="title"
                  placeholder="Title..."
                  type="text"
                  id="typeText"
                  className="form-control"
                />
              </div>
            </div>
            <div className="my-2">
              <textarea
                onChange={handleOnChange}
                placeholder="Your Story here..."
                name="description"
                style={{ borderRadius: '0px', width: '100%' }}
                className="form-control bord"
                id="textAreaExample"
                rows="5"
              ></textarea>
            </div>
            <div className="col-md-1 my-1 px-2">
              <button onClick={()=>handleOnSavePost()} className="btn btn-primary my-1">Publish</button>
            </div>
          </div>
        </div>
      </form>
    </>
  );
}
