import React, { useState } from 'react'

export default function Blogs() {

  const [Blogs , setBlogs] = useState()



  // Filterning the Blogs old and new 
  const handleOnFilterBlogs = async(By)=>{
    try {
      console.log(By)
      const data = await fetch(`http://127.0.0.1:8000/Blog/FilterBlogs/${By}` , {
        method:"GET"
      })
  
      const res = await data.json()
      console.log(res.data)

      if(res.status==="Success"){
        setBlogs(res.data)
      }
      

    } catch (error) {
      console.log(error.message)
    }    
  }

  return (
    <>
    {/* Search and filter option for the blogs */}
    <div className="SearchAndFilterBox  p-1">
      <div className="row">

        <div className="col-md-6">
          <input type="text" placeholder='Search By Category' className='w-100 p-2 my-1'  style={{borderRadius:"2px" , border:"none" , backgroundColor:"whitesmoke" , color:"black" , outline:"lightgray solid 1px"}}/>
        </div>

        <div style={{textAlign:"end"}} className="col-md-6">
          <div className="dropdown">
            <button className='btn btn-secondary dropdown-toggle my-1' type='button' data-bs-toggle="dropdown" aria-expanded="false" >Filter Blogs By</button>
            <ul className='dropdown-menu'>
              <li className='p-2 border' onClick={()=>{handleOnFilterBlogs("asc")}} style={{cursor:"pointer"}} >Latest</li>
              <li className='p-2 border' onClick={()=>{handleOnFilterBlogs('desc')}} style={{cursor:"pointer"}} >Oldest</li>
            </ul>
          </div>
        </div>

      </div>
    </div>



    {/* Here we will render all the Blogs  */}
    <div className="container border border-dark my-2 p-2">
      <div className="row">

        {/* We will map here */}
        {Blogs && Blogs.length > 0 ? Blogs.map((data,index)=>{
          return <div key={data._id} className='col-md-4' > 

<div className="card my-2 my-5" style={{ borderRadius: "15px", boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)" }}>
      <span style={{ color: "white", padding: "8px 15px", borderRadius: "15px 0 0 0", backgroundColor: "#2e2e2e" }} className="position-absolute top-0 start-50 translate-middle">
        <span>{data.categories}</span>
      </span>



      <img style={{ borderRadius: "0px", objectFit: "cover", height: "200px" }} className="card-img-top" src={`http://127.0.0.1:8000/${data.picture.replace(/\\/g, '/')}`} alt="Card image cap" />
      <div className="card-body">
        <h5 className="card-title">{data.title}</h5>
        <p className="card-text">
          {data.description.toString().split(' ').length > 15
            ? data.description.toString().split(' ').slice(0, 15).join(' ') + '...'
            : data.description}
        </p>

        <button className="btn btn-dark">Read More</button>

        <div className="mt-3">
          <p className="card-text"><small className="text-muted">By: {data.username}</small></p>
          <p className="card-text"><small className="text-muted">Created: {data.createdAt}</small></p>
        </div>
      </div>
    </div>

          </div>
        }) :<h1 className='my-4' style={{textAlign:"center"}} >No Blogs</h1>}

      </div>
    </div>
    </>
  )
}
