import React, { useState, useEffect } from 'react';
import { Link, useSearchParams, useHistory } from 'react-router-dom';
import Post from '../Blogs/Post';
import '../../css/Categories.css';

export default function Categories() {
  const data = [
    { id: 1, type: 'All' },
    { id: 2, type: 'Music' },
    { id: 3, type: 'Movies' },
    { id: 4, type: 'Sports' },
    { id: 5, type: 'Tech' },
    { id: 6, type: 'Fashion' },
    { id: 7, type: 'Food' },
    { id: 8, type: 'Entertainment' },
    { id: 9, type: 'Games' },
  ];

  const [searchParams] = useSearchParams();
  const category = searchParams.get('category');
  const history = useHistory();

  useEffect(() => {
    // Scroll to the 'blogs' section when a category is clicked
    const targetElement = document.getElementById('blogs');
    if (targetElement) {
      targetElement.scrollIntoView({ behavior: 'smooth' });
    }
  }, [category]);

  return (
    <>
      <div style={{ textAlign: 'start' }} className="btnDiv container">
        <Link to={`/create?category=${category || ''}`}>
          <button
            style={{
              fontSize: '20px',
              fontWeight: '600',
              letterSpacing: '3px',
              backgroundColor: '#2e2e2e',
              width: '100%',
              borderRadius: '3px',
              color: 'white',
              padding: '15px 0px',
            }}
            className="my-3 btn"
          >
            CREATE BLOG
          </button>
        </Link>
      </div>

      {/* Table Component */}
      <div className="container-fluid">
        <div className="row">
          {/* Box-1 */}
          <div className="CategoryBox col-md-3">
            <div>
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th scope="col">
                      <div>
                        <Link
                          to={'/'}
                          style={{
                            textDecoration: 'none',
                            color: 'black',
                          }}
                        >
                          <span style={{ fontSize: '30px' }}>All Categories</span>
                        </Link>
                      </div>
                    </th>
                  </tr>
                </thead>
                <tbody>
                  {data.map((data) => (
                    <tr key={data.id}>
                      <th scope="row">
                        <a href="blogs">
                          {' '}
                          <Link
                            hrefLang="blogs"
                            to={`/?category=${data.type}`}
                            style={{
                              textDecoration: 'none',
                              color: 'black',
                              display: 'block',
                            }}
                          >
                            {data.type}
                          </Link>
                        </a>
                      </th>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
          {/* Box-1 */}

          {/* --------------------------------Box-2 */}
          <div className="col-md-9">
            <Post id="blogs" />
          </div>
        </div>
      </div>
    </>
  );
}
