import React, { useContext, useState, useEffect } from 'react';
import '../../css/CreatePost.css';
import { useLocation, useNavigate } from 'react-router-dom';
import { InfoContext } from '../../Context/InfoProvider';
import { useParams } from 'react-router-dom';

export default function Update() {
  const handleOnChange = (e) => {
    setPostData({ ...postData, [e.target.name]: e.target.value });
  };

  const [postData, setPostData] = useState({
    title: '',
    description: '',
    picture: '',
    username: '',
    categories: '',
    createdDate: '',
  });
  const location = useLocation();
  const { Account } = useContext(InfoContext);
  const navigate = useNavigate();
  const { id } = useParams();
  const [file, setFile] = useState();

  useEffect(() => {
    const token = sessionStorage.getItem('accessToken');

    const getInfoWithId = async () => {
      const data = await fetch(`http://127.0.0.1:8000/Blog/postDetail/${id}`, {
        method: 'GET',
        headers: {
          Authorization: token,
        },
      });

      const res = await data.json();
      setPostData(res.info);
    };

    getInfoWithId();
  }, [id]);

  const handleOnUpdatePost = async (e) => {
    e.preventDefault();
    console.log('Post Updated');

    postData.username = Account.name;
    postData.createdDate = new Date().toLocaleDateString();
    postData.categories = location.search?.split('=')[1] || 'All';

    const token = sessionStorage.getItem('accessToken');

    const data = await fetch(`http://127.0.0.1:8000/Blog/UpdatePost/${id}`, {
      method: 'PUT',
      body: JSON.stringify(postData),
      headers: {
        'Content-type': 'application/json',
        Authorization: `${token}`,
      },
    });

    const res = await data.json();
    console.log(res);

    if (res.status === 'Updated') {
      console.log('Done');
      navigate(`/postDetail/${id}`);
    }
  };

  const handleOnAddImage = (e) => {
    const uploadedFile = e.target.files[0];
    setFile(uploadedFile);

    // Show the uploaded image
    const imageUrl = URL.createObjectURL(uploadedFile);
    setPostData({ ...postData, picture: imageUrl });
  };

  return (
    <>
      <div className="CreatePost container text-center bg-image-CreatePost">
        <img className='my-2' src={postData.picture} alt="Image" />
      </div>

      <form onSubmit={handleOnUpdatePost}>
        <div className="container">
          <div className="row my-2 p-2">
            <div className="col-md-1">
              <div className="my-1 p-1">
                <label style={{ cursor: 'pointer' }} htmlFor="fileinput">
                  <i className="fa-solid mx-2 fa-upload fa-2x" style={{ color: 'gray' }}></i>
                  <input
                    className="border border-primary"
                    id="fileinput"
                    type="file"
                    style={{ display: 'none' }}
                    onChange={handleOnAddImage}
                  />
                </label>
              </div>
            </div>
            <div className="col-md-10">
              <div>
                <input
                  onChange={handleOnChange}
                  style={{ borderRadius: '0px', width: '100%' }}
                  name="title"
                  placeholder="Title..."
                  type="text"
                  id="typeText"
                  className="form-control"
                  value={postData.title || ''}
                />
              </div>
            </div>
            <div className="my-2">
              <textarea
                onChange={handleOnChange}
                placeholder="Your Story here..."
                name="description"
                style={{ borderRadius: '0px', width: '100%' }}
                className="form-control bord"
                id="textAreaExample"
                rows="5"
                value={postData.description || ''}
              ></textarea>
            </div>
            <div className="col-md-1 my-1 px-2">
              <button type="submit" className="btn btn-primary my-1">
                Update
              </button>
            </div>
          </div>
        </div>
      </form>
    </>
  );
}
